generated.css:
* {
  margin: 0;
  padding: 0;
  border: 0;
  outline: 0;
  font-size: 100%;
  vertical-align: baseline;
}

body {
  font-family: "Avenir", Helvetica, sans-serif;
  font-size: 16px;
  width: 100%;
}

h1 {
  font-size: 48px;
  font-weight: 100;
  color: #2B9DFF;
}

h3 {
  font-size: 24px;
}

p {
  font-size: 16px;
}

.main-button {
  display: block;
  text-decoration: none;
  color: #2B9DFF;
  border: 2px solid #2B9DFF;
  border-radius: 6px;
  width: 175px;
  height: 55px;
  line-height: 55px;
  text-align: center;
}

.cta .cta-btn {
  display: block;
  text-decoration: none;
  color: #fff;
  border-radius: 6px;
  width: 175px;
  height: 55px;
  line-height: 55px;
  text-align: center;
  background-color: #2B9DFF;
}

nav {
  line-height: 150px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
nav a {
  text-decoration: none;
  color: #202427;
  font-size: 20px;
  font-weight: 700;
}
nav .logo img {
  height: 80px;
  width: 80px;
  border-radius: 50%;
}

header {
  height: 1000px;
  min-height: 100px;
  padding: 0 5%;
  background-image: url("ba96484d345af440c7548e75189f8b81.png");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: bottom right;
}
header h1 {
  width: 40%;
  margin: 2em 0 0 0;
}

.team {
  text-align: center;
  margin: 10em 0 3em 0;
  padding: 0 5%;
}
.team h1 {
  width: 50%;
  margin: 0 auto 30px;
}
.team-members {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}
.team-members__member {
  color: #202427;
}
.team-members__member h3 {
  margin-bottom: 20px;
}

.about {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 5%;
  height: 700px;
  min-height: 700px;
}
.about h1 {
  margin-bottom: 20px;
}
.about-icons {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}
.about-icons img {
  height: 80px;
  width: 80px;
}
.about-img {
  height: 100%;
}

.cta {
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: space-between;
  max-width: 700px;
  margin: 20px auto;
}
.cta h1, .cta a {
  display: inline-block;
  margin: 0 60px;
  vertical-align: middle;
}

footer {
  padding: 20px 10%;
  margin-bottom: 30px;
  color: #2B9DFF;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
footer .footer-pages {
  display: flex;
  justify-content: space-around;
}
footer .footer-pages a {
  color: #2B9DFF;
  margin-right: 10px;
}
